import { Injectable } from '@angular/core';
import { ApiService } from './api.service';
import type { Category, CreateCategoryDto, UpdateCategoryDto } from '../../models/category.model';
import { Observable } from 'rxjs';

@Injectable({ providedIn: 'root' })
export class CategoryService {
  private readonly path = '/api/categories';

  constructor(private api: ApiService) {}

  getAll(): Observable<Category[]> {
    return this.api.get<Category[]>(this.path);
  }

  getById(id: string): Observable<Category> {
    return this.api.get<Category>(`${this.path}/${id}`);
  }

  create(dto: CreateCategoryDto): Observable<Category> {
    return this.api.post<Category>(this.path, dto);
  }

  update(id: string, dto: UpdateCategoryDto): Observable<Category> {
    return this.api.put<Category>(`${this.path}/${id}`, dto);
  }

  delete(id: string): Observable<int> {
    return this.api.delete(`${this.path}/${id}`);
  }
}
