import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { CategoryService } from '../../../core/services/category.service';
import type { Category } from '../../../models/category.model';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-category-list',
  standalone: true,
  imports: [CommonModule, RouterLink],
  templateUrl: './category-list.component.html',
  styleUrl: './category-list.component.scss',
})
export class CategoryListComponent implements OnInit {
  categories: Category[] = [];
  loading = true;
  error: string | null = null;

  constructor(private categoryService: CategoryService,private toastr: ToastrService) {}

  ngOnInit(): void {
    this.load();
  }

  load(): void {
    this.loading = true;
    this.error = null;
    this.categoryService.getAll().subscribe({
      next: (data) => {
        this.categories = data;
        this.loading = false;
      },
      error: (err) => {
        this.error = err?.message ?? 'Failed to load categories';
        this.loading = false;
      },
    });
  }

delete(id: string, name: string): void {
  if (!confirm(`Delete category "${name}"?`)) return;

  this.categoryService.delete(id).subscribe({
    next: () => {
      this.toastr.success(`Category "${name}" deleted successfully`, 'Success');
      this.load();
    },
    error: (err) => {
      this.toastr.error(err?.message ?? 'Delete failed', 'Error');
    },
  });

  }
}
<div class="container">
  <h1>Categories</h1>
  <p>
    <a routerLink="/categories/new" class="btn btn-primary">New Category</a>
  </p>
  @if (error) {
    <p class="error">{{ error }}</p>
  }
  @if (loading) {
    <p>Loading...</p>
  } @else {
    <table>
      <thead>
        <tr>
          <th>Name</th>
          <th>Description</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        @for (c of categories; track c.id) {
          <tr>
            <td>{{ c.name }}</td>
            <td>{{ c.description ?? '-' }}</td>
            <td>
              <a [routerLink]="['/categories', c.id, 'edit']" class="btn btn-secondary">Edit</a>
              <button type="button" class="btn btn-danger" (click)="delete(c.id, c.name)">Delete</button>
            </td>
          </tr>
        }
      </tbody>
    </table>
    @if (categories.length === 0 && !error) {
      <p>No categories yet. Create one above.</p>
    }
  }
</div>
