import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router, RouterLink, RouterLinkActive, RouterOutlet } from '@angular/router';
import { AuthService } from './core/services/auth.service';
import { ToastrModule, ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, RouterOutlet, RouterLink, RouterLinkActive],
  templateUrl: './app.component.html',
  styleUrl: './app.component.scss',
})
export class AppComponent {
  title = 'MiniCommerce';
  isLoginPage = false;

  constructor(
    public auth: AuthService,
    private router: Router
  ) {
    this.updateLoginPage();
    this.router.events.subscribe(() => this.updateLoginPage());
  }

  private updateLoginPage(): void {
    const url = this.router.url;
    this.isLoginPage = url === '/' || url === '' || url.startsWith('/?');
  }

  logout(): void {
    this.auth.logout();
  }
}
@if (!isLoginPage) {
  <header class="app-header">
    <div class="container header-inner">
      <a routerLink="/" class="logo">{{ title }}</a>
      <nav>
        <a routerLink="/home" routerLinkActive="active" [routerLinkActiveOptions]="{ exact: true }">Home</a>
        <a routerLink="/categories" routerLinkActive="active">Categories</a>
        <a routerLink="/products" routerLinkActive="active">Products</a>
        @if (auth.isSsoEnabled && auth.isAuthenticated) {    
          <button type="button" class="btn btn-secondary" (click)="logout()">Logout</button>
          <span class="btn btn-secondary">{{ auth.name }}</span>
        } @else {
          <a routerLink="/" routerLinkActive="active" [routerLinkActiveOptions]="{ exact: true }">Login (SSO)</a>
        }
      </nav>
    </div>
  </header>
  <main>
    <router-outlet />
  </main>
} @else {
  <router-outlet />
}
