import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-confirm-modal',
  standalone: true,
  imports: [CommonModule], // <-- مهم عشان *ngIf و [ngClass]
  templateUrl: './confirm-dialog.component.html',
  styleUrls: ['./confirm-dialog.component.scss'],
})
export class ConfirmModalComponent {
  @Input() id: string = 'confirmModal'; // لازم يكون موجود
  @Input() title: string = 'Confirm Delete';
  @Input() message: string = 'Are you sure?';
  @Input() onConfirm!: () => void;

  isOpen = false;

  open() {
    this.isOpen = true;
  }

  hide() {
    this.isOpen = false;
  }

  confirm() {
    if (this.onConfirm) this.onConfirm();
    this.hide();
  }
}
<div
  id="{{ id }}"
  class="modal-backdrop"
  [ngClass]="{ show: isOpen }"
  *ngIf="isOpen"
>
  <div class="modal-content-wrapper">
    <div class="modal-header">
      <h5 class="modal-title">{{ title }}</h5>
      <button type="button" class="btn-close" (click)="hide()"></button>
    </div>
    <div class="modal-body">
      <p>{{ message }}</p>
    </div>
    <div class="modal-footer">
      <button class="btn btn-secondary" (click)="hide()">Cancel</button>
      <button class="btn btn-danger" (click)="confirm()">Delete</button>
    </div>
  </div>
</div>
