import { Component, OnInit, ViewChild } from '@angular/core';
import { CategoryService } from '../../../core/services/category.service';
import { ToastrService } from 'ngx-toastr';
import type { Category } from '../../../models/category.model';
import { ConfirmModalComponent } from '../../../Shared/Dialog/confirm-dialog.component';

@Component({
  selector: 'app-category-list',
  standalone: true,
  imports: [ConfirmModalComponent],
  templateUrl: './category-list.component.html',
  styleUrls: ['./category-list.component.scss'],
})
export class CategoryListComponent implements OnInit {
  categories: Category[] = [];
  selectedCategory: Category | null = null;
  error: string | null = null; // <--- ضيفي ده

  @ViewChild('confirmModal') confirmModal!: ConfirmModalComponent;

  constructor(
    private categoryService: CategoryService,
    private toastr: ToastrService
  ) {}

  ngOnInit(): void {
    this.load();
  }

  load(): void {
    this.error = null; // reset error
    this.categoryService.getAll().subscribe({
      next: (data) => (this.categories = data),
      error: (err) => this.error = err?.message ?? 'Failed to load',
    });
  }

  openDeleteModal(category: Category) {
    this.selectedCategory = category;
    this.confirmModal.onConfirm = () => this.confirmDelete();
    this.confirmModal.open();
  }

  confirmDelete() {
    if (!this.selectedCategory) return;
    this.categoryService.delete(this.selectedCategory.id).subscribe({
      next: () => {
        this.toastr.success(`Category "${this.selectedCategory?.name}" deleted successfully`);
        this.load();
      },
      error: (err) => this.toastr.error(err?.message ?? 'Delete failed'),
    });
  }
}

<div class="container">
  <h1>Categories</h1>
  <p>
    <a routerLink="/categories/new" class="btn btn-primary">New Category</a>
  </p>

  <p *ngIf="error" class="text-danger">{{ error }}</p>
  <p *ngIf="loading">Loading...</p>

  <table *ngIf="!loading && categories.length > 0" class="table table-bordered">
    <thead>
      <tr>
        <th>Name</th>
        <th>Description</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let c of categories; trackBy: trackById">
        <td>{{ c.name }}</td>
        <td>{{ c.description ?? '-' }}</td>
        <td>
          <a [routerLink]="['/categories', c.id, 'edit']" class="btn btn-secondary">Edit</a>
          <button type="button" class="btn btn-danger" (click)="openDeleteModal(c)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>

  <p *ngIf="!loading && categories.length === 0 && !error">
    No categories yet. Create one above.
  </p>
</div>

<app-confirm-modal
  #confirmModal
  [id]="'deleteModal'"
  [title]="'Confirm Delete'"
  [message]="'Are you sure you want to delete this category?'"
></app-confirm-modal>
