import { Injectable } from '@angular/core';
import { ApiService } from './api.service';
import type { Product, CreateProductDto, UpdateProductDto } from '../../models/product.model';
import { Observable } from 'rxjs';

@Injectable({ providedIn: 'root' })
export class ProductService {
  private readonly path = '/api/products';

  constructor(private api: ApiService) {}

  getAll(): Observable<Product[]> {
    return this.api.get<Product[]>(this.path);
  }

  getById(id: string): Observable<Product> {
    return this.api.get<Product>(`${this.path}/${id}`);
  }

  getByCategoryId(categoryId: string): Observable<Product[]> {
    return this.api.get<Product[]>(`${this.path}/by-category/${categoryId}`);
  }

  create(dto: CreateProductDto): Observable<Product> {
    return this.api.post<Product>(this.path, dto);
  }

  update(id: string, dto: UpdateProductDto): Observable<Product> {
    return this.api.put<Product>(`${this.path}/${id}`, dto);
  }

  delete(id: string): Observable<void> {
    return this.api.delete(`${this.path}/${id}`);
  }
}
