import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { Router, RouterLink, ActivatedRoute } from '@angular/router';
import { ProductService } from '../../../core/services/product.service';
import { CategoryService } from '../../../core/services/category.service';
import type { CreateProductDto } from '../../../models/product.model';
import type { Category } from '../../../models/category.model';

@Component({
  selector: 'app-product-form',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, RouterLink],
  templateUrl: './product-form.component.html',
  styleUrl: './product-form.component.scss',
})
export class ProductFormComponent implements OnInit {
  form!: FormGroup;
  id: string | null = null;
  categories: Category[] = [];
  loading = false;
  error: string | null = null;

  constructor(
    private fb: FormBuilder,
    private productService: ProductService,
    private categoryService: CategoryService,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.id = this.route.snapshot.paramMap.get('id');
    this.form = this.fb.group({
      name: ['', [Validators.required, Validators.maxLength(200)]],
      description: [''],
      price: [0, [Validators.required, Validators.min(0)]],
      stockQuantity: [0, [Validators.required, Validators.min(0)]],
      categoryId: ['', Validators.required],
    });
    this.categoryService.getAll().subscribe({
      next: (list) => (this.categories = list),
      error: () => (this.error = 'Failed to load categories'),
    });
    if (this.id) {
      this.productService.getById(this.id).subscribe({
        next: (p) =>
          this.form.patchValue({
            name: p.name,
            description: p.description ?? '',
            price: p.price,
            stockQuantity: p.stockQuantity,
            categoryId: p.categoryId,
          }),
        error: () => (this.error = 'Product not found'),
      });
    }
  }

  get isEdit(): boolean {
    return this.id != null;
  }

  submit(): void {
    if (this.form.invalid || this.loading) return;
    this.loading = true;
    this.error = null;
    const dto: CreateProductDto = {
      name: this.form.value.name,
      description: this.form.value.description || null,
      price: Number(this.form.value.price),
      stockQuantity: Number(this.form.value.stockQuantity),
      categoryId: this.form.value.categoryId,
    };
    const req = this.id
      ? this.productService.update(this.id, dto)
      : this.productService.create(dto);
    req.subscribe({
      next: () => this.router.navigate(['/products']),
      error: (err) => {
        this.error = err?.error?.message ?? err?.message ?? 'Save failed';
        this.loading = false;
      },
    });
  }
}
<div class="container">
  <h1>{{ isEdit ? 'Edit Product' : 'New Product' }}</h1>
  <p><a routerLink="/products" class="btn btn-secondary">Back to list</a></p>
  @if (error) {
    <p class="error">{{ error }}</p>
  }
  <form [formGroup]="form" (ngSubmit)="submit()">
    <div class="form-group">
      <label for="name">Name</label>
      <input id="name" type="text" formControlName="name" />
      @if (form.get('name')?.invalid && form.get('name')?.touched) {
        <span class="field-error">Name is required.</span>
      }
    </div>
    <div class="form-group">
      <label for="description">Description</label>
      <textarea id="description" formControlName="description" rows="3"></textarea>
    </div>
    <div class="form-group">
      <label for="categoryId">Category</label>
      <select id="categoryId" formControlName="categoryId">
        <option value="">-- Select category --</option>
        @for (c of categories; track c.id) {
          <option [value]="c.id">{{ c.name }}</option>
        }
      </select>
      @if (form.get('categoryId')?.invalid && form.get('categoryId')?.touched) {
        <span class="field-error">Category is required.</span>
      }
    </div>
    <div class="form-group">
      <label for="price">Price</label>
      <input id="price" type="number" step="0.01" formControlName="price" />
      @if (form.get('price')?.invalid && form.get('price')?.touched) {
        <span class="field-error">Valid price required.</span>
      }
    </div>
    <div class="form-group">
      <label for="stockQuantity">Stock quantity</label>
      <input id="stockQuantity" type="number" formControlName="stockQuantity" />
      @if (form.get('stockQuantity')?.invalid && form.get('stockQuantity')?.touched) {
        <span class="field-error">Valid quantity required.</span>
      }
    </div>
    <button type="submit" class="btn btn-primary" [disabled]="form.invalid || loading">
      {{ loading ? 'Saving...' : (isEdit ? 'Update' : 'Create') }}
    </button>
  </form>
</div>
