import { Component, OnInit, ViewChild } from '@angular/core';
import { CategoryService } from '../../../core/services/category.service';
import { ToastrService } from 'ngx-toastr';
import type { Category } from '../../../models/category.model';
import { ConfirmModalComponent } from 'src/app/Shared/Dialog/confirm-dialog';

@Component({
  selector: 'app-category-list',
  standalone: true,
  imports: [ConfirmModalComponent],
  templateUrl: './category-list.component.html',
  styleUrls: ['./category-list.component.scss'],
})
export class CategoryListComponent implements OnInit {
  categories: Category[] = [];
  selectedCategory: Category | null = null;


  constructor(
    private categoryService: CategoryService,
    private toastr: ToastrService
  ) {}

  ngOnInit(): void {
    this.load();
  }

  load(): void {
    this.categoryService.getAll().subscribe({
      next: (data) => (this.categories = data),
      error: (err) => this.toastr.error(err?.message ?? 'Failed to load'),
    });
  }

  openDeleteModal(category: Category) {
    this.selectedCategory = category;
    this.confirmModal.onConfirm = () => this.confirmDelete();
    this.confirmModal.open();
  }

  confirmDelete() {
    if (!this.selectedCategory) return;
    this.categoryService.delete(this.selectedCategory.id).subscribe({
      next: () => {
        this.toastr.success(
          `Category "${this.selectedCategory?.name}" deleted successfully`
        );
        this.load();
      },
      error: (err) => this.toastr.error(err?.message ?? 'Delete failed'),
    });
  }
}
