import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-confirm-modal',
  standalone: true,
  templateUrl: './confirm-dialog.component.html',
  styleUrls: ['./confirm-dialog.component.scss'], 
})
export class ConfirmModalComponent {
  @Input() id: string = 'confirmModal';
  @Input() title: string = 'Confirm Delete';
  @Input() message: string = 'Are you sure?';
  @Input() onConfirm!: () => void;

  private modalEl!: HTMLElement;

  ngAfterViewInit() {
    // نعطي reference للـ modal element
    this.modalEl = document.getElementById(this.id)!;
  }

  confirm() {
    if (this.onConfirm) this.onConfirm();
    this.hide();
  }

  open() {
    this.show();
  }

  // استخدام CSS classes بدل bootstrap JS
  private show() {
    if (!this.modalEl) return;
    this.modalEl.classList.add('show');
    this.modalEl.style.display = 'block';
    this.modalEl.removeAttribute('aria-hidden');
    this.modalEl.setAttribute('aria-modal', 'true');
    this.modalEl.classList.add('d-block');
    document.body.classList.add('modal-open');
  }

  private hide() {
    if (!this.modalEl) return;
    this.modalEl.classList.remove('show', 'd-block');
    this.modalEl.style.display = 'none';
    this.modalEl.removeAttribute('aria-modal');
    this.modalEl.setAttribute('aria-hidden', 'true');
    document.body.classList.remove('modal-open');
  }
}
<div
  id="{{ id }}"
  class="modal-backdrop"
  [ngClass]="{ show: isOpen }"
  *ngIf="isOpen"
>
  <div class="modal-content-wrapper">
    <div class="modal-header">
      <h5 class="modal-title">{{ title }}</h5>
      <button type="button" class="btn-close" (click)="hide()"></button>
    </div>
    <div class="modal-body">
      <p>{{ message }}</p>
    </div>
    <div class="modal-footer">
      <button class="btn btn-secondary" (click)="hide()">Cancel</button>
      <button class="btn btn-danger" (click)="confirm()">Delete</button>
    </div>
  </div>
</div>
